import { Component, OnInit } from '@angular/core';
import { Utente } from 'src/app/model/utente/utente';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css']
})
export class HomePageComponent implements OnInit{
  utente?: Utente;

  videoPath?: string;
  largeCardTitle?: string;
  largeCardText?: string;
  largeCardDescription?: string;

  constructor() {}

  ngOnInit(): void {
    // Qui decidiamo card e video iniziale
    this.setSelectedCard(this.card2);
    this.videoPath = "/assets/Video/Mare_Video_2.mp4";
    console.log(this.videoPath);
  }

  selectedCard: any;

  setSelectedCard(card: any) {
    this.selectedCard = card;
  }

  setVideoBackground(percorsoVideo: string){
    this.videoPath = percorsoVideo;


    console.log(this.videoPath);

  }


  card1 = {
    videoPath: '/assets/Video/Animal_Video_1.mp4',
    largeCardTitle: 'Sierra Desert - Marocco',
    largeCardText: 'Marrakech Merzouga',
    largeCardDescription: 'Sabbia dorata e cieli stellati abbracciano il Marrakech Merzouga.'
  };

  card2 = {
    videoPath: '/assets/Video/Mare_Video_2.mp4',
    largeCardTitle: 'Bali - Indonesia',
    largeCardText: 'Isola di Bali',
    largeCardDescription: 'Paradiso tropicale, spiagge incantevoli, Bali dimora di antichi dei e verdi risaie.'
  };

  card3 = {
    videoPath: '/assets/Video/Niagara.mp4',
    largeCardTitle: 'Niagara Falls - USA',
    largeCardText: 'Cascate del Niagara',
    largeCardDescription: 'Potenza e bellezza in un\'incredibile sinfonia di acqua e natura.'
  };

  card4 = {
    videoPath: '/assets/Video/Cappadocia_Video_1.mp4',
    largeCardTitle: 'Cappadocia - Turchia',
    largeCardText: 'Valle di Goreme',
    largeCardDescription: 'Magia e mistero in un paesaggio incantato di roccia e storia, tra le meraviglie della Cappadocia, tra sogno e realtà.'
  };


}
<section id="wrapper">
    <div class="header-homepage">


      <div id="video">
        <video id="background-video" [src]="videoPath" type="video/mp4"  [muted]="'muted'" loop autoplay  >
        </video>
      </div>

      <div id="inner-wrapper" class="container">
        <div id="content-wrapper" class="js-content-wrapper">
          <section id="main">
            <div id="content" class="page-content">
              <section class="home-page d-flex justify-content-center align-items-center">

                <div id="contenuto-uno">
                  <section class="card-lg__container">
                    <div class="card__content">
                        <p class="card__text-sm">{{ selectedCard?.largeCardTitle }}</p>
                        <h1 class="card__text-lg">{{ selectedCard?.largeCardText }}</h1>
                        <p class="card__text-md">{{ selectedCard?.largeCardDescription }}</p>
                        <a href="/listapost" class="card__btn ">Esplora</a>
                    </div>
                  </section>
                </div>

                <div id="contenuto-due">
                  <section class="card-sm__container">

                    <section class="card-sm" (click)="setSelectedCard(card1)" (click)="setVideoBackground('/assets/Video/Animal_Video_1.mp4')" style="background-image:url('/assets/Immagini/Deserto_1.jpg')">
                      <p class="card__text-sm" id="Marocco-s">Sierra Desert - Marocco</p>
                      <h1 class="card__text-lg" id="Marocco-m">Marrakech Merzouga</h1>
                    </section>

                    <section class="card-sm" (click)="setSelectedCard(card2)" (click)="setVideoBackground('/assets/Video/Mare_Video_2.mp4')" style="background-image: url('/assets/Immagini/Blog_3.jpg'); background-position: center right;">
                      <p class="card__text-sm" id="Bali-s">Bali - Indonesia</p>
                      <h1 class="card__text-lg" id="Bali-m">Isola di Bali</h1>
                    </section>

                    <section class="card-sm" (click)="setSelectedCard(card3)" (click)="setVideoBackground('/assets/Video/Niagara.mp4')" style="background-image: url('/assets/Immagini/Galleria_7.jpg')">
                      <div class="card-content-wrapper">
                        <p class="card__text-sm" id="Niagara-s">Niagara Falls - USA</p>
                        <h1 class="card__text-lg" id="Niagara-m" >Cascate del Niagara</h1>                      
                      </div>                      
                    </section>

                    <section class="card-sm" (click)="setSelectedCard(card4)" (click)="setVideoBackground('/assets/Video/Cappadocia_Video_1.mp4')" style="background-image: url('/assets/Immagini/Cappadocia_1.jpg')">
                      <p class="card__text-sm" id="Turchia-s">Cappadocia - Turchia</p>
                      <h1 class="card__text-lg" id="Turchia-m">Valle di Goreme</h1>
                    </section>

                  </section>

                </div>

              </section>
            </div>
          </section>
        </div>
      </div>        
    </div>

    <div class="footer-homepage">
      <h1>Ciao</h1>
    </div>


  </section>
