import { HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { forkJoin } from 'rxjs';
import { Categoria } from 'src/app/model/categoria/categoria';
import { Post } from 'src/app/model/post/post';
import { Utente } from 'src/app/model/utente/utente';
import { CategoriaService } from 'src/app/services/categoria-Service/categoria.service';
import { PostService } from 'src/app/services/post-Service/post.service';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';

@Component({
  selector: 'app-lista-post',
  templateUrl: './lista-post.component.html',
  styleUrls: ['./lista-post.component.css']
})

export class ListaPostComponent implements OnInit{
  listaPost: Post[] = [];
  utente: Utente = new Utente();
  listaPostBackup: Post[] = [];
  categorie?: Categoria[] = [];

  currentPage: number = 1;

  constructor(private route: ActivatedRoute, private service: PostService, private serviceUtente:UtenteService,
     private router: Router, private serviceCategoria: CategoriaService){

  }
  ngOnInit(): void {
    this.utente = JSON.parse(sessionStorage.getItem('utenteloggato')!);
    this.service.getAllPosts()
      .subscribe(
        {
          next: (response: Post[]) => {
            this.listaPost = response;
            this.listaPostBackup = response;

            this.serviceCategoria.getAllCategorie().subscribe({
              next: data => {
                console.log(data);
                this.categorie = data;
              },
              error: error => console.log(error)
            })
          },
          error: (error: HttpErrorResponse) => {
            alert(error.message)
          }
        }
      );
  }

  aggiungiPreferito(post: Post){
    this.serviceUtente.addPreferitoUtenteById(post, this.utente!.id_utente!)
    .subscribe({
      next: () => {
        console.log('Post aggiunto ai preferiti con successo.');
        this.router.navigate(['/','listapost']).then
            (() => window.location.reload());        
      },
      error: error => console.log(error)
    });
  }


  noPost: boolean = false;

  ricercaPostPerTitolo(key: string): void {
    console.log(key);
    const results: Post[] = [];
    for (const post of this.listaPost) {
      if (post.titolo!.toLowerCase().indexOf(key.toLowerCase()) !== -1) {
        results.push(post);
      }
    }
    //aggiorniamo i post visibili sulla pagina
    this.listaPost = results;
    //se non c'è alcuna ricerca effettuata o il risultato è nullo ridammi tutti i post
    if (!key) {
      this.listaPost = this.listaPostBackup;
    }

    if (results.length === 0 && key) {
      this.noPost = true;
    } else {
      this.noPost = false;
    }
  }

  // forkJoin è un operatore di combinazione in RxJS che combina gli ultimi valori emessi da più Observable in 
  // un unico Observable, ma solo quando tutti gli Observable hanno completato. In pratica, attende che tutti gli 
  // Observable passati come argomenti abbiano emesso un valore e siano stati completati prima di restituire i 
  // risultati combinati.

  // Nel contesto del tuo codice, forkJoin viene utilizzato per affrontare il problema delle chiamate asincrone 
  // sovrapposte all'interno del ciclo for nel metodo ricercaPostPerAutore.

  // ogni chiamata asincrona a this.service.getAutorePostById otterrà i dati relativi a un post specifico. 
  // Quando tutti i dati saranno stati ottenuti, l'Observable restituito da forkJoin emetterà un array con tutti 
  // i nomi degli autori corrispondenti ai post

  ricercaPostPerAutore(key: string): void {
      console.log(key);
      const observables = this.listaPost.map(post => this.service.getAutorePostById(post.id_post!));

      forkJoin(observables).subscribe({
        next: (autori: string[]) => {
          //costante di appoggio per creare la lista e travasare il risultato in listaPost
          const results: Post[] = [];
          for (let i = 0; i < autori.length; i++) {
            const autore = autori[i].trim(); //perchè io nel JSON faccio restituire esplicitamente nome + " " + cognome
            console.log(autore);

            if (autore.toLowerCase().indexOf(key.toLowerCase()) !== -1) {
              results.push(this.listaPost[i]);
            }
          }

          this.listaPost = results;

          if (results.length === 0  && key) {
            this.noPost = true;
          } else {
            this.noPost = false;
          }
        },
        error: (err) => console.log(err)
      });

      //deve stare fuori dal ciclo for, se non c'è alcuna ricerca effettuata o il risultato è nullo ridammi tutti i post
      if (!key) {
        this.listaPost = this.listaPostBackup;
      }

    }

    ricercaPostPerCategoria(key: string){



    }


}
<section id="wrapper">

  <header class="page-header">
    <h1 class="h1 page-title">
      <span>I Racconti dei Viaggiatori</span>
    </h1>

    <div class="d-flex">

      <div style="padding-right: 20px;">
        <form class="form-inline my-2 my-lg-0" style="display: inline;">
          <input type="search" (ngModelChange)="ricercaPostPerTitolo(key.value)" #key="ngModel" ngModel
           name="key" id="searchTitolo" class="form-control mr-sm-2" placeholder="Ricerca per Titolo" required>
        </form>
      </div>

      <div style="padding-right: 20px;">
        <form class="form-inline my-2 my-lg-0" style="display: inline;">
          <input type="search" (ngModelChange)="ricercaPostPerAutore(key2.value)" #key2="ngModel" ngModel
           name="key2" id="searchAutore" class="form-control mr-sm-2" placeholder="Ricerca per Autore" required>
        </form>        
      </div>

      <div style="padding-right: 20px;">
        <form class="form-inline my-2 my-lg-0" style="display: inline;">
          <select type="search" (ngModelChange)="ricercaPostPerCategoria(key3.value)" #key3="ngModel" ngModel
          name="key3" id="searchCategoria" class="form-select" placeholder="Ricerca per Categoria" required>
            <option disabled selected>Seleziona una Categoria</option>
            <ng-container *ngFor="let categoria of categorie">
                <option [ngValue]="categoria">{{categoria!.nome_categoria}}</option>
            </ng-container>
          </select>
        </form> 
      </div>

    </div>    

    <div *ngIf="noPost" class="col-lg-12 col-md-12 col-xl-12">
      <div class="alert alert-info" role="alert">
        <h4 class="alert-heading">NESSUN ARTICOLO TROVATO!</h4>
        <p>No Posts were found.</p>
      </div>
    </div>

  </header>

  <!-- tramite la angular class indichiamo che la classe cambia nel momento in cui no-posts diventa true
  e noPost diventa true quando non ci sono posts da mostrare. -->
  <div class="container" [ngClass]="{'no-posts': noPost}">
    <div class="row">
      <!-- paginate: Questa è la pipe di paginazione fornita dal modulo ngx-pagination. Una pipe è una funzione 
        che può essere utilizzata per trasformare i dati prima di visualizzarli. La pipe paginate gestisce la 
        suddivisione dei dati in pagine in base ai parametri specificati. L'oggetto che passiamo alla pipe è tra le {} -->
      <div class="col-12 col-md-4" *ngFor="let post of (listaPost | paginate: { itemsPerPage: 9, currentPage: currentPage })!">

        <app-singolo-post [post]="post"></app-singolo-post>

        <div *ngIf="this.utente" id="contenuto" style="margin-left: 5px;">
          <button type="button" class="btn btn-outline-success" (click)="aggiungiPreferito(post)">Aggiungi ai Preferiti</button>
        </div>

      </div>
    </div>
  </div>

  <div class="pagination-container">
    <pagination-controls (pageChange)="currentPage = $event"></pagination-controls>
  </div>

</section>
