import { HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Utente } from 'src/app/model/utente/utente';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';

//Qui sono specificati i file che sono direttamente connessi con questo e quindi 
//accedono direttamente a quanto dichiarato qui
@Component({
  selector: 'app-lista-utenti',
  templateUrl: './lista-utenti.component.html',
  styleUrls: ['./lista-utenti.component.css']
})
export class ListaUtentiComponent implements OnInit{
  utenti?: Utente[];

  constructor(private route: ActivatedRoute, private service: UtenteService, private router: Router) { }

  //quando viene chiamato il componente esegui il metodo descritto
  ngOnInit(): void{
    this.reloadData();
  }

  reloadData(): void {
    this.service.getAllUtenti()
        .subscribe({
          next: (response: Utente[]) => {
            this.utenti = response;
          },
          error: (error: HttpErrorResponse) => {
            alert(error.message)
          }
        });
  }

  dettagliUtente(id?: number){
    this.router.navigate(["dettagliutente", id]);
  }

  rimuoviUtente(id?: number){ 
    if (id) {
      this.service.deleteUtenteById(id).subscribe({
        next: () => {
          console.log('Utente eliminato con successo.');
          this.reloadData(); // Aggiorna la lista degli utenti dopo l'eliminazione
        },
        error: error => console.log(error)
      });
    }
  }
}
<section id="wrapper">

  <header class="page-header">
    <h1 class="h1 page-title">
      <span>Il tuo account</span>
    </h1>
  </header>

  <table class="table table-striped">

    <thead>
      <tr>
        <th>Nome</th>
        <th>Cognome</th>
        <th>Email</th>
        <th>Actions</th>
      </tr>
    </thead>

    <tbody>
      <!-- ngFor mi permette di ciclare sull'array di oggetti ottenuto in questo caso gli utenti -->
      <tr *ngFor="let utente of utenti">
        <td>{{ utente.nome }}</td>
        <td>{{ utente.cognome }}</td>
        <td>{{ utente.email }}</td>
        <td>
          <!-- In questo punto sta ciclando sull'array di utenti e per ognuno crea il pulsante.
          Vengono immagazzinati per ognuno i dati del singolo utente e da questo riconosce quale utente è-->
          <button (click)="dettagliUtente(utente.id_utente)"
              class="btn btn-info" style="margin-left: 10px">Details</button>
          <button (click)="rimuoviUtente(utente.id_utente)"
              class="btn btn-info" style="margin-left: 10px">Remove</button>
        </td>
      </tr>
    </tbody>

  </table>

</section>
