import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Utente } from 'src/app/model/utente/utente';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';

@Component({
  selector: 'app-login-utente',
  templateUrl: './login-utente.component.html',
  styleUrls: ['./login-utente.component.css']
})
export class LoginUtenteComponent implements OnInit{
  utente: Utente = new Utente();

  constructor(private service: UtenteService, private router: Router) { }

  ngOnInit(): void {
    const video = document.getElementById('background-video') as HTMLVideoElement;
    video.play();
  }

  onSubmit() : void {
    console.log(this.utente);
    this.accessoUtente();
  }

  private accessoUtente() {
      this.service.loginUtente(this.utente)
          .subscribe({
            next: data => {
              console.log(data);
              sessionStorage.setItem('utenteloggato', JSON.stringify(data))
              this.utente = new Utente();
              this.router.navigate(['/','homepage']).then
              (() => window.location.reload());
            },
            error: error => console.log(error)
          });
  }

  //mi serve un metodo per dire che quando la pagina si ricarica con refresh il video deve ripartire
}
<section id="wrapper">
  <div id="video">
    <video id="background-video" loop muted>
      <source src="/assets/Video/Mare_Video_1.mp4" type="video/mp4">
    </video>
  </div>
  <div id="inner-wrapper" class="container">
    <div id="content-wrapper" class="js-content-wrapper">
      <section id="main">
        <div id="content" class="page-content">
          <section class="login-form d-flex justify-content-center align-items-center">
            <div class="form-signin w-100 m-auto">
              <form>
                <div>
                  <h1 class="h3 mb-3 fw-normal" style="display: inline;">Welcome Back</h1>
                  <img class="mb-4" src="/assets/Immagini/Ship_logo_2.png" alt="" width="72" height="57">
                </div>

                <div class="form-floating">
                  <input type="email" class="form-control" id="floatingInput" placeholder="name@example.com">
                  <label for="floatingInput">Email</label>
                </div>
                <div class="form-floating">
                  <input type="password" class="form-control" id="floatingPassword" placeholder="Password">
                  <label for="floatingPassword">Password</label>
                </div>

                <div class="form-check text-start my-3">
                  <input class="form-check-input" type="checkbox" value="remember-me" id="flexCheckDefault">
                  <label class="form-check-label" for="flexCheckDefault">
                    Remember me
                  </label>
                </div>
                <button class="btn btn-primary w-100 py-2" type="submit">Sign in</button>
                <p class="mt-5 mb-3 text-body-secondary">Ciao</p>
              </form>
            </div>
          </section>
        </div>
      </section>
    </div>
  </div>
</section>
