import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { throwError } from 'rxjs';
import { Categoria } from 'src/app/model/categoria/categoria';
import { Immagine } from 'src/app/model/immagine/immagine';
import { Post } from 'src/app/model/post/post';
import { Utente } from 'src/app/model/utente/utente';
import { CategoriaService } from 'src/app/services/categoria-Service/categoria.service';
import { ImmagineService } from 'src/app/services/immagine-Service/immagine.service';
import { PostService } from 'src/app/services/post-Service/post.service';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';


@Component({
  selector: 'app-i-miei-post',
  templateUrl: './i-miei-post.component.html',
  styleUrls: ['./i-miei-post.component.css']
})
export class IMieiPostComponent implements OnInit{
  utente?: Utente;
  currentPost?: Post;
  newPost: Post = new Post();
  categorie?: Categoria[] = [];
  immagineInserita: Immagine = new Immagine();

  constructor(private route: ActivatedRoute, private service: UtenteService, private servicePost: PostService, 
    private serviceCategoria: CategoriaService, private serviceImmagine: ImmagineService,
    private router: Router, private http: HttpClient) {}

  ngOnInit(): void {
    this.utente = JSON.parse(sessionStorage.getItem('utenteloggato')!)
    this.service
      .getArticoliUtenteById(this.utente!.id_utente!)
      .subscribe(
        {
          next: data => {
                  console.log(data);
                  this.utente = data;
                },
          error: error => console.log(error)
        }
      );

      this.serviceCategoria.getAllCategorie().subscribe({
        next: data => {
          console.log(data);
          this.categorie = data;
        },
        error: error => console.log(error)
      })    
  }

  activateAddPost: boolean = false;

  aggiuntaPost(){    
    this.activateAddPost = true;
    window.scrollTo({
      top: 0,
      behavior: 'smooth' // Scorrimento fluido
    });
  }  

  activateUpdatePost: boolean = false;

  aggiornamentoPost(postVecchio: Post){
    this.currentPost = postVecchio;
    this.activateUpdatePost = true;
    window.scrollTo({
      top: 0,
      behavior: 'smooth' // Scorrimento fluido
    });
  }

  eliminazionePost(id_post?: number){
    if (id_post) {
      this.servicePost.deletePostById(id_post)
      .subscribe({
        next: () => {
          console.log('Post eliminato con successo.');
          this.router.navigate(['/','mieiarticoli']).then
              (() => window.location.reload());

        },
        error: error => console.log(error)
      });
    }
  }



  onSubmitAdd(){
    console.log(this.newPost);
    this.savePost();
  }

  selectedCategory!: Categoria;

  uploadedImageURL!: string;

  erroreTitolo: boolean = false;
  messaggioErroreTitolo: string = 'Inserisci un Titolo.';

  erroreContenuto: boolean = false;
  messaggioErroreContenuto: string = 'Inserisci un Contenuto: assicurati di aver inserito almeno 400 caratteri (inclusi spazi e punteggiatura).';

  erroreCategoria: boolean = false;
  messaggioErrore: string = 'Seleziona una categoria.';

  erroreImmagine: boolean = false;
  messaggioErroreImmagine: string = 'Inserisci un URL per l\'immagine.';

  savePost(){
    //controllo Titolo in aggiunta post
    if (!this.newPost.titolo) {
      this.erroreTitolo = true;
      return;
    }
    this.erroreTitolo = false;

    //controllo Contenuto in aggiunta post
    if (!this.newPost.contenuto || this.newPost.contenuto.length < 400) {
      this.erroreContenuto = true;
      return;
    }
    this.erroreContenuto = false;

    //controllo categoria in aggiunta post
    if (!this.selectedCategory) {
      this.erroreCategoria = true;
      return;
    }
    this.erroreCategoria = false;

    //controllo immagine in aggiunta post
    if (!this.uploadedImageURL) {
      this.erroreImmagine = true;
      return;
    }
    this.erroreImmagine = false;

    this.newPost.data = this.getCurrentDate();

    this.servicePost.addPost(this.newPost, this.utente!.id_utente!)
          .subscribe({
            next: (data) => {
              console.log(data);
              this.newPost = new Post();

              //aggiunta della categoria al nuovo post
              this.servicePost.addCategoriePostById(data.id_post!, this.selectedCategory)
                .subscribe({
                  next: (postData) => {
                    console.log('Categoria assegnata al post:', postData);

                    //inserimento dell'immagine nel database, al pari di noi che aggiungiamo in modo HardCoded 
                    //le categorie e conseguente associazione al post
                    this.immagineInserita.url = this.uploadedImageURL;

                    this.serviceImmagine.addImmagine(postData.id_post!,this.immagineInserita)
                    .subscribe({
                      next: (response) => {
                        console.log('Immagine inserita nel DB e associata al post:', response);
                        this.router.navigate(['/','mieiarticoli']).then
                        (() => window.location.reload());
                      },
                      error: (err) => console.log(err)
                    });
                  },

                  error: (err) => console.log(err)
                });
            },

            error: (err) => console.log(err)
          });
  }

  getCurrentDate(): Date {
    return new Date();
  }

  onSubmitUpdate(){
    console.log(this.currentPost);
    this.aggiornaPost();
  }

  updatedCategoria!: Categoria;

  aggiornaPost(){
    //controllo Titolo in aggiunta post
    if (!this.currentPost!.titolo) {
      this.erroreTitolo = true;
      return;
    }
    this.erroreTitolo = false;

    //controllo Contenuto in aggiunta post
    if (!this.currentPost!.contenuto || this.currentPost!.contenuto.length < 400) {
      this.erroreContenuto = true;
      return;
    }
    this.erroreContenuto = false;

    //controllo categoria in aggiornamento post
    if (!this.updatedCategoria) {
      this.erroreCategoria = true;
      return;
    }
    this.erroreCategoria = false;

    this.servicePost.updatePost(this.currentPost!, this.utente!.id_utente!)
      .subscribe(
        {
          next: data => {
                  console.log(data);
                  this.currentPost = data;

                  //aggiornamento della categoria al post
                  this.servicePost.addCategoriePostById(data.id_post!, this.updatedCategoria)
                  .subscribe({
                    next: (postData) => {
                      console.log('Categoria associata al post aggiornata:', postData);
                      this.router.navigate(['/','mieiarticoli']).then
                      (() => window.location.reload());
                    },

                    error: (err) => console.log(err)
                  });
                },

          error: (err) => console.log(err)
        });
  }

  closePopUp(){
    this.activateUpdatePost = false;
    this.activateAddPost = false;
  }

}
<section id="wrapper">
    <div class="wrapper">

        <header class="page-header" >
            <h1 class="h1 page-title">
              <span>I tuoi articoli</span>
            </h1>
        </header>

        <div style="padding-top: 10px; margin-left: 10px;">
            <button type="button" class="btn btn-outline-success" (click)="aggiuntaPost()">Aggiungi Post</button>
        </div>

        <div class="container" *ngFor="let post of utente?.articoli">
            <app-singolo-post [post]="post"></app-singolo-post>

            <div style="padding-bottom: 30px;">
                <button style="margin-right: 20px; margin-left: 10px;" type="button" class="btn btn-outline-danger" (click)="eliminazionePost(post?.id_post)">Rimuovi Post</button>
                <button type="button" class="btn btn-outline-warning" (click)="aggiornamentoPost(post)">Aggiorna Post</button>
            </div>
        </div>

        <!--  gli <ng-container> non sono destinati a essere utilizzati per il posizionamento degli elementi sulla pagina.  -->
        <!-- dobbiamo usare un div -->
        <!-- Aggiornamento di un Post -->
        <div class="overlay-container" *ngIf="activateUpdatePost">
            <section class="card-lg__container">
                <div class="card__content">
                    <form (ngSubmit)="onSubmitUpdate()" class="row g-3">                       

                        <div style="padding-bottom: 10px;">
                            <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 512 512" style="float: right; cursor: pointer" (click)="closePopUp()">
                                <path d="M256 48a208 208 0 1 1 0 416 208 208 0 1 1 0-416zm0 464A256 256 0 1 0 256 0a256 256 0 1 0 0 512zM175 175c-9.4 9.4-9.4 24.6 0 33.9l47 47-47 47c-9.4 9.4-9.4 24.6 0 33.9s24.6 9.4 33.9 0l47-47 47 47c9.4 9.4 24.6 9.4 33.9 0s9.4-24.6 0-33.9l-47-47 47-47c9.4-9.4 9.4-24.6 0-33.9s-24.6-9.4-33.9 0l-47 47-47-47c-9.4-9.4-24.6-9.4-33.9 0z"/>
                            </svg> 
                            <br>
                            <h3 class="h3 mb-3 fw-normal" style="display: inline; font-family: fantasy; font-size: xxx-large;">Aggiornamento Post</h3>
                        </div>

                        <div class="col-md-4">
                            <div class="form-floating" >
                              <input type="text" class="form-control input-md" id="floatingTitolo" placeholder="Titolo" required [(ngModel)]="currentPost!.titolo" name="titolo" />
                              <label for="floatingInput">Titolo</label>
                            </div>
                        </div>

                        <div *ngIf="erroreTitolo" class="alert alert-danger">{{ messaggioErroreTitolo }}</div>

                        <div class="col-md-4">
                            <div class="form-floating" >
                              <textarea type="text" class="form-control input-md" id="floatingContenuto" placeholder="Contenuto" required [(ngModel)]="currentPost!.contenuto" name="contenuto"></textarea>
                              <label for="floatingInput">Contenuto</label>
                            </div>
                        </div>

                        <div *ngIf="erroreContenuto" class="alert alert-danger">{{ messaggioErroreContenuto }}</div>

                        <select class="form-select" name="subject" aria-label="Subject" required [(ngModel)]="updatedCategoria" id="selectCategoria">
                            <option disabled selected>Seleziona una Categoria</option>
                            <ng-container *ngFor="let categoria of categorie">
                                <option [ngValue]="categoria">{{categoria!.nome_categoria}}</option>
                            </ng-container>
                        </select>

                        <!-- Abbiamo rimosso l'aggiornamento per le immagini, in quanto le immagini devono essere
                        gestite in modo separato, con possibilità di aggiunta, rimozione senza aggiornamento
                        servirà quindi un tasto del tipo "aggiorna immagini" che permetterà all'utente sia di 
                        rimuoverle che di aggiungerle. Il tutto dipende dalla chiave esterna di immagine, questa connette
                        direttamente il post all'immagine e per aggiornare un'immagine, questa va rimossa.-->

                        <div *ngIf="erroreCategoria" class="alert alert-danger">{{ messaggioErrore }}</div>

                        <button class="btn btn-primary w-100 py-2" type="submit" value="updatingPost" >Aggiorna</button>
                    </form>
                </div>
            </section>
        </div>

        <!-- Aggiunta di un Post -->
        <div class="overlay-container" *ngIf="activateAddPost">
            <section class="card-lg__container">
                <div class="card__content">
                    <form (ngSubmit)="onSubmitAdd()" class="row g-3">

                        <div style="padding-bottom: 10px;">
                            <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 512 512" style="float: right; cursor: pointer" (click)="closePopUp()">
                                <path d="M256 48a208 208 0 1 1 0 416 208 208 0 1 1 0-416zm0 464A256 256 0 1 0 256 0a256 256 0 1 0 0 512zM175 175c-9.4 9.4-9.4 24.6 0 33.9l47 47-47 47c-9.4 9.4-9.4 24.6 0 33.9s24.6 9.4 33.9 0l47-47 47 47c9.4 9.4 24.6 9.4 33.9 0s9.4-24.6 0-33.9l-47-47 47-47c9.4-9.4 9.4-24.6 0-33.9s-24.6-9.4-33.9 0l-47 47-47-47c-9.4-9.4-24.6-9.4-33.9 0z"/>
                            </svg> 
                            <br>
                            <h3 class="h3 mb-3 fw-normal" style="display: inline; font-family: fantasy; font-size: xxx-large;">Aggiungi un Post</h3>
                        </div>

                        <div class="col-md-4">
                            <div class="form-floating" >
                                <input type="text" class="form-control input-md" id="floatingTitolo" placeholder="Titolo" required [(ngModel)]="newPost.titolo" name="titolo" />
                                <label for="floatingInput">Titolo</label>
                            </div>
                        </div>

                        <div *ngIf="erroreTitolo" class="alert alert-danger">{{ messaggioErroreTitolo }}</div>

                        <div class="col-md-4">
                            <div class="form-floating" >
                              <textarea type="text" class="form-control input-md" id="floatingContenuto" placeholder="Contenuto" required [(ngModel)]="newPost.contenuto" name="contenuto"></textarea>
                              <label for="floatingInput">Contenuto</label>
                            </div>
                        </div>

                        <div *ngIf="erroreContenuto" class="alert alert-danger">{{ messaggioErroreContenuto }}</div>

                            <!-- Nel codice fornito, [(ngModel)]="newPost.categorie" collega l'elemento <select>
                                 all'oggetto newPost.categorie nel componente. Ciò significa che quando viene 
                                 selezionata un'opzione nell'elenco, il valore selezionato viene assegnato a 
                                 newPost.categorie e viceversa. -->
                                 <!-- D'altra parte, ngValue è utilizzata all'interno del tag <option> per assegnare
                                     un valore specifico all'opzione. In questo caso, categoria è un oggetto 
                                     all'interno dell'array categorie. ngValue consente di impostare categoria come 
                                     valore dell'opzione, rendendo disponibile l'intero oggetto quando l'opzione 
                                     viene selezionata. -->
                        <select class="form-select" name="subject" aria-label="Subject" required [(ngModel)]="selectedCategory" id="selectCategoria">
                            <option disabled selected>Seleziona una Categoria</option>
                            <ng-container *ngFor="let categoria of categorie">
                                <option [ngValue]="categoria">{{categoria!.nome_categoria}}</option>
                            </ng-container>
                        </select>

                        <div *ngIf="erroreCategoria" class="alert alert-danger">{{ messaggioErrore }}</div>

                        <div class="col-md-4">
                            <div class="form-floating" >
                              <textarea type="text" class="form-control input-md" id="floatingImmagine" placeholder="Immagine" required [(ngModel)]="uploadedImageURL" name="contenuto"></textarea>
                              <label for="floatingInput">WEB-URL Immagine</label>
                            </div>
                        </div>

                        <div *ngIf="erroreImmagine" class="alert alert-danger">{{ messaggioErroreImmagine }}</div>

                        <button class="btn btn-primary w-100 py-2" type="submit" value="addingPost">Aggiungi</button>
                    </form>

                </div>
            </section>
        </div>     

    </div>
</section>
