import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Utente } from 'src/app/model/utente/utente';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class UtenteService {

  //private http: HttpClient;
  private baseUrl = 'http://localhost:8080/api/v1';

  // angular dependency injection - di tipo constructor injection
  constructor(private http: HttpClient) { }

  // lo strano singolo apice si chiama backtick alt-96
  addUtente(utente: Object): Observable<Object> {
    return this.http.post(`${this.baseUrl}/add/u`, utente);
  }

  loginUtente(utente: Object): Observable<Object> {
    return this.http.post(`${this.baseUrl}/add/u`, utente);
  }
}
