import { Categoria } from 'src/app/model/categoria/categoria';
import { Post } from 'src/app/model/post/post';
import { Utente } from 'src/app/model/utente/utente';
import { CategoriaService } from 'src/app/services/categoria-Service/categoria.service';
import { PostService } from 'src/app/services/post-Service/post.service';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';


@Component({
  selector: 'app-lista-post',
  templateUrl: './lista-post.component.html',
  styleUrls: ['./lista-post.component.css']
})

export class ListaPostComponent implements OnInit{
  listaPost: Post[] = [];
  utente: Utente = new Utente();
  listaPostBackup: Post[] = [];
  categorie?: Categoria[] = [];
  listapostPreferiti: Post[] = [];

  currentPage: number = 1;

  constructor(private route: ActivatedRoute, private service: PostService, private serviceUtente:UtenteService,
     private router: Router, private serviceCategoria: CategoriaService){

      // l'attributo [(ngModel)] sta tentando di legare il valore selezionato nel <select> con la variabile 
      // searchCategoria nella tua componente Angular. Quando si utilizza l'approccio di two-way data binding con 
      // [(ngModel)], l'opzione con disabled selected può non funzionare correttamente per l'opzione predefinita 
      // del select.

      // Per risolvere il problema, puoi provare a inizializzare la variabile searchCategoria con un valore 
      // predefinito nella componente Angular o nel suo costruttore. In questo modo, il valore corrispondente 
      // sarà selezionato di default nel select.
      this.searchCategoria = ''; //risolviamo con valore di Default

  }
  ngOnInit(): void {
    this.utente = JSON.parse(sessionStorage.getItem('utenteloggato')!);
    this.service.getAllPosts()
      .subscribe(
        {
          next: (response: Post[]) => {
            this.listaPost = response;
            this.listaPostBackup = response;

            this.serviceCategoria.getAllCategorie().subscribe({
              next: data => {
                data.sort((a, b) => a.nome_categoria!.localeCompare(b.nome_categoria!)); //ordine alfabetico categorie
                this.categorie = data;

                this.serviceUtente.getPreferitiUtenteById(this.utente!.id_utente!)
                .subscribe({
                  next: (risultati) => {
                    this.listapostPreferiti = risultati.postPreferiti!;
                    this.controlloPreferiti();
                  },
                  error: (err) => console.log(err)
                })
              },
              error: error => console.log(error)
            })
          },
          error: (error: HttpErrorResponse) => {
            alert(error.message)
          }
        }
      );
  }

  toggleBookmark(post: Post){
    if(post.isFavorite){
      this.serviceUtente.removePreferitoUtenteById(post.id_post!, this.utente!.id_utente!)
      .subscribe({
        next: () => {
          console.log('Post rimosso con successo.');
          post.isFavorite = false;
          return;          
        },
        error: error => console.log(error)
      });

    } else {
      this.serviceUtente.addPreferitoUtenteById(post, this.utente!.id_utente!)
      .subscribe({
        next: () => {
          console.log('Post aggiunto ai preferiti con successo.');
          post.isFavorite = true;
          return;
        },
        error: error => console.log(error)
      });
    }
  }

  noPost: boolean = false;

  ricercaPostPerTitolo(key: string): void {
    console.log(key);
    const results: Post[] = [];
    for (const post of this.listaPost) {
      if (post.titolo!.toLowerCase().indexOf(key.toLowerCase()) !== -1) {
        results.push(post);
      }
    }
    //aggiorniamo i post visibili sulla pagina
    this.listaPost = results;
    //se non c'è alcuna ricerca effettuata o il risultato è nullo ridammi tutti i post
    if (!key) {
      this.noPost = false;
      this.listaPost = this.listaPostBackup;
    }

    if (results.length === 0 && key) {
      this.noPost = true;
    }
  }

  // forkJoin è un operatore di combinazione in RxJS che combina gli ultimi valori emessi da più Observable in 
  // un unico Observable, ma solo quando tutti gli Observable hanno completato. In pratica, attende che tutti gli 
  // Observable passati come argomenti abbiano emesso un valore e siano stati completati prima di restituire i 
  // risultati combinati.

  // Nel contesto del tuo codice, forkJoin viene utilizzato per affrontare il problema delle chiamate asincrone 
  // sovrapposte all'interno del ciclo for nel metodo ricercaPostPerAutore.

  // ogni chiamata asincrona a this.service.getAutorePostById otterrà i dati relativi a un post specifico. 
  // Quando tutti i dati saranno stati ottenuti, l'Observable restituito da forkJoin emetterà un array con tutti 
  // i nomi degli autori corrispondenti ai post

  ricercaPostPerAutore(key: string): void {
    console.log(key);

    //risulta necessario posizionarlo prima
    if (!key) {
      this.noPost = false;      
      this.listaPost = this.listaPostBackup;
    }

    const observables = this.listaPost.map(post => this.service.getAutorePostById(post.id_post!));

    forkJoin(observables).subscribe({
      next: (autori: string[]) => {
        //costante di appoggio per creare la lista e travasare il risultato in listaPost
        const results: Post[] = [];
        for (let i = 0; i < autori.length; i++) {
          const autore = autori[i].trim(); //perchè io nel JSON faccio restituire esplicitamente nome + " " + cognome
          console.log(autore);

          if (autore.toLowerCase().indexOf(key.toLowerCase()) !== -1) {
            results.push(this.listaPost[i]);
          }
        }

        this.listaPost = results;

        if (results.length === 0  && key) {
          this.noPost = true;
        }

      },
      error: (err) => console.log(err)
    });
  }

  categoryPost?: string;
  searchCategoria?: string;

  ricercaPostPerCategoria(key: string): void {
    console.log(key);
    const results: Post[] = [];
    const observables = this.listaPost.map(post => this.service.getCategoriePostById(post.id_post!));

    forkJoin(observables).subscribe({
      next: (categorie: string[]) => {
        for (let i = 0; i < categorie.length; i++) {
          this.categoryPost = categorie[i];

          if (this.categoryPost!.toLowerCase().indexOf(key.toLowerCase()) !== -1) {
            results.push(this.listaPost[i]);
          }
        }

        //aggiorniamo i post visibili sulla pagina
        this.listaPost = results;

        //se non c'è alcuna ricerca effettuata o il risultato è nullo ridammi tutti i post
        if (!key) {
          this.listaPost = this.listaPostBackup;
        }

        if (results.length === 0 && key) {
          this.noPost = true;
        } else {
          this.noPost = false;
        }
      },
      error: (err) => console.log(err)
    });
  }

  orderBy?: string;
  cresc?: boolean = false;

  ordinamentoPost(tipoOrdinamento: string) {
    if (tipoOrdinamento === 'position' && this.cresc === true) {
      this.listaPost.sort((a, b) => a.id_post! - b.id_post!);
    } else if(tipoOrdinamento === 'position' && this.cresc === false){
      this.listaPost.sort((a, b) => b.id_post! - a.id_post!);
    }

    if (tipoOrdinamento === 'name' && this.cresc === true) {
      this.listaPost.sort((a, b) => b.titolo!.localeCompare(a.titolo!)); 
      // confronto lessicografico di stringhe in ordine crescente
    } else if (tipoOrdinamento === 'name' && this.cresc === false) {
      this.listaPost.sort((a, b) => a.titolo!.localeCompare(b.titolo!)); 
      // confronto lessicografico di stringhe in ordine decrescente
    }

    if (tipoOrdinamento === 'date' && this.cresc === false) {
      this.listaPost.sort((a, b) => {
        const dateA = new Date(a.data!).getTime();
        const dateB = new Date(b.data!).getTime();
        console.log('Date A:', dateA, 'Date B:', dateB);
        return dateB - dateA; //prima il piu recente così
        //Quando si sottrae dateA da dateB, il risultato sarà un valore positivo se la data di dateB è 
        //successiva a dateA. Questo significa che, nell'ordinamento decrescente, i post con la data di 
        //inserimento più recente verranno posizionati prima nella lista.
      });
    } else if (tipoOrdinamento === 'date' && this.cresc === true) {
      this.listaPost.sort((a, b) => {
        const dateA = new Date(a.data!).getTime();
        const dateB = new Date(b.data!).getTime();
        return dateA - dateB; //prima il piu recente così
        //Quando si sottrae dateA da dateB, il risultato sarà un valore positivo se la data di dateB è 
        //successiva a dateA. Questo significa che, nell'ordinamento decrescente, i post con la data di 
        //inserimento più recente verranno posizionati prima nella lista.
      });
    }
  }

  cambioOrdine(changeOrder: string){
    if(this.cresc === true){
      this.cresc = false;
    } else if(this.cresc === false){
      this.cresc = true;
    }

    this.ordinamentoPost(changeOrder);
  }

  espandiFiltri?: boolean = false;

  openFilter(){
    this.espandiFiltri = true;
  }

  closeFilter(){
    this.espandiFiltri = false;
  }

  controlloPreferiti(){
    for (const postPreferito of this.listapostPreferiti) {
      const postCorrispondente = this.listaPost.find(post => post.id_post === postPreferito.id_post);
      if (postCorrispondente) {
        postCorrispondente.isFavorite = true;
      }
    }
  }

}
<section id="wrapper">

  <header class="page-header">
    <h1 class="h1 page-title">
      <span>I Racconti dei Viaggiatori</span>
    </h1>
  </header>

  <div class="d-flex" style="align-items: center; padding-bottom: 30px;">

    <!-- Filtri a sinistra -->
    <div style="padding-left: 20px; display: flex; align-items: center;">
      <button class="learn-more" (click)="openFilter()">
        <span class="circle" aria-hidden="true">
        <span class="icon arrow"></span>
        </span>
        <span class="button-text">Search</span>
      </button>
    </div>

    <!-- Spazio flessibile vuoto per separare i filtri dall'ordinamento -->
    <div class="flex-grow-1"></div>

    <!-- Ordinamento a destra -->
    <div class="toolbar-sorter sorter">

        <label class="sorter-label" for="sorter" >Ordina articoli per </label>

        <select id="sorter" data-role="sorter" class="sorter-options" required [(ngModel)]="orderBy" (change)="ordinamentoPost(orderBy!)" style="margin-right: 20px;">
            <option class="sorter-option" value="position" selected="selected">Posizione</option>
            <option class="sorter-option" value="name">Titolo Articolo</option>
            <option class="sorter-option" value="date">Data Inserimento</option>
        </select>

        <div *ngIf="!cresc" class="change-order" (click)="cambioOrdine(orderBy!)">
            <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 576 512" style="padding-right: 20px;">
              <path d="M151.6 42.4C145.5 35.8 137 32 128 32s-17.5 3.8-23.6 10.4l-88 96c-11.9 13-11.1 33.3 2 45.2s33.3 11.1 45.2-2L96 146.3V448c0 17.7 14.3 32 32 32s32-14.3 32-32V146.3l32.4 35.4c11.9 13 32.2 13.9 45.2 2s13.9-32.2 2-45.2l-88-96zM320 480h32c17.7 0 32-14.3 32-32s-14.3-32-32-32H320c-17.7 0-32 14.3-32 32s14.3 32 32 32zm0-128h96c17.7 0 32-14.3 32-32s-14.3-32-32-32H320c-17.7 0-32 14.3-32 32s14.3 32 32 32zm0-128H480c17.7 0 32-14.3 32-32s-14.3-32-32-32H320c-17.7 0-32 14.3-32 32s14.3 32 32 32zm0-128H544c17.7 0 32-14.3 32-32s-14.3-32-32-32H320c-17.7 0-32 14.3-32 32s14.3 32 32 32z"/>
            </svg>
        </div>

        <div *ngIf="cresc" style="cursor: pointer; display: contents; padding-right: 20px;" (click)="cambioOrdine(orderBy!)">
            <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 576 512" style="padding-right: 20px;">
              <path d="M151.6 469.6C145.5 476.2 137 480 128 480s-17.5-3.8-23.6-10.4l-88-96c-11.9-13-11.1-33.3 2-45.2s33.3-11.1 45.2 2L96 365.7V64c0-17.7 14.3-32 32-32s32 14.3 32 32V365.7l32.4-35.4c11.9-13 32.2-13.9 45.2-2s13.9 32.2 2 45.2l-88 96zM320 480c-17.7 0-32-14.3-32-32s14.3-32 32-32h32c17.7 0 32 14.3 32 32s-14.3 32-32 32H320zm0-128c-17.7 0-32-14.3-32-32s14.3-32 32-32h96c17.7 0 32 14.3 32 32s-14.3 32-32 32H320zm0-128c-17.7 0-32-14.3-32-32s14.3-32 32-32H480c17.7 0 32 14.3 32 32s-14.3 32-32 32H320zm0-128c-17.7 0-32-14.3-32-32s14.3-32 32-32H544c17.7 0 32 14.3 32 32s-14.3 32-32 32H320z"/>
            </svg>
        </div>

    </div>

  </div>

  <!-- Parte centrale Filtri/Post -->
  <div class="filter-posts-container">

    <div class="filters-column" style="min-height: 300vh;" *ngIf="espandiFiltri">

      <div class="close-filter">
        <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 512 512" style="float: right; cursor: pointer;" (click)="closeFilter()">
          <path d="M256 48a208 208 0 1 1 0 416 208 208 0 1 1 0-416zm0 464A256 256 0 1 0 256 0a256 256 0 1 0 0 512zM175 175c-9.4 9.4-9.4 24.6 0 33.9l47 47-47 47c-9.4 9.4-9.4 24.6 0 33.9s24.6 9.4 33.9 0l47-47 47 47c9.4 9.4 24.6 9.4 33.9 0s9.4-24.6 0-33.9l-47-47 47-47c9.4-9.4 9.4-24.6 0-33.9s-24.6-9.4-33.9 0l-47 47-47-47c-9.4-9.4-24.6-9.4-33.9 0z"/>
        </svg> 
      </div>

      <div class="containerSearch">

        <div class="labelSearch">
          <strong>
            Titolo
          </strong>
        </div>

        <div class="form">
          <input type="text" (ngModelChange)="ricercaPostPerTitolo(key.value)" #key="ngModel" ngModel
           name="key" id="searchTitolo" class="input" placeholder="Ricerca per Titolo" required>
          <span class="input-border"></span>
        </div>

      </div>

      <div class="containerSearch">

        <div class="labelSearch">
          <strong>
            Autore
          </strong>
        </div>

        <div class="form">
            <input type="text" (ngModelChange)="ricercaPostPerAutore(key2.value)" #key2="ngModel" ngModel
             name="key2" id="searchAutore" class="input" placeholder="Ricerca per Autore" required>
             <span class="input-border"></span>
        </div>

      </div>

      <div class="containerSearch">

        <div class="labelSearch">
          <strong>
            Categoria
          </strong>
        </div>

        <div>
          <form class="form-inline my-2 my-lg-0" style="display: inline;">
              <select class="form-select custom-select" name="subject" aria-label="Subject" required [(ngModel)]="searchCategoria" id="selectCategoria">
                <option value="" disabled selected hidden>Ricerca per Categoria</option>
                <option value="" disabled selected >Seleziona una Categoria</option>
                <ng-container *ngFor="let categoria of categorie">
                  <option [ngValue]="categoria.nome_categoria">{{categoria!.nome_categoria}}</option>
                </ng-container>
              </select>
          </form>
        </div>

        <div style="padding-top: 15px; text-align-last: center;">
          <button class="fancy" (click)="ricercaPostPerCategoria(searchCategoria!)" >
            <span class="top-key"></span>
            <span class="text">Avvia Ricerca</span>
            <span class="bottom-key-1"></span>
            <span class="bottom-key-2"></span>
          </button>
        </div>

      </div>

    </div>

    <div class="posts-column">

      <!-- Messaggio Nessun Post trovato -->
      <div *ngIf="noPost" class="col-lg-12 col-md-12 col-xl-12">
        <div class="alert alert-info" role="alert">
          <h4 class="alert-heading">NESSUN ARTICOLO TROVATO!</h4>
          <p>No Posts were found.</p>
        </div>
      </div>

      <!-- tramite la angular class indichiamo che la classe cambia nel momento in cui no-posts diventa true
      e noPost diventa true quando non ci sono posts da mostrare. -->
      <div class="container" [ngClass]="{'no-posts': noPost}">
        <div class="row">
          <!-- paginate: Questa è la pipe di paginazione fornita dal modulo ngx-pagination. Una pipe è una funzione
            che può essere utilizzata per trasformare i dati prima di visualizzarli. La pipe paginate gestisce la
            suddivisione dei dati in pagine in base ai parametri specificati. L'oggetto che passiamo alla pipe è tra le {} -->
          <div class="col-12 col-md-4" *ngFor="let post of (listaPost | paginate: { itemsPerPage: 9, currentPage: currentPage })!">

            <div *ngIf="this.utente" id="contenutoBookmark">
              <label class="ui-bookmark" >
                <input type="checkbox" (change)="toggleBookmark(post)" [checked]="post.isFavorite" >
                 <div class="bookmark"><!-- Icona-->
                  <!-- <svg viewBox="0 0 32 32">
                    <g>
                      <path d="M27 4v27a1 1 0 0 1-1.625.781L16 24.281l-9.375 7.5A1 1 0 0 1 5 31V4a4 4 0 0 1 4-4h14a4 4 0 0 1 4 4z"></path>
                    </g>
                  </svg> -->
                  <g>
                    <path d="M0 48C0 21.5 21.5 0 48 0l0 48V441.4l130.1-92.9c8.3-6 19.6-6 27.9 0L336 441.4V48H48V0H336c26.5 0 48 21.5 48 48V488c0 9-5 17.2-13 21.3s-17.6 3.4-24.9-1.8L192 397.5 37.9 507.5c-7.3 5.2-16.9 5.9-24.9 1.8S0 497 0 488V48z"/>


                  </g>
                  <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 384 512">
                    </svg>

                </div>
              </label>
            </div>

            <app-singolo-post [post]="post"></app-singolo-post>



          </div>

        </div>
      </div>

    </div>

  </div>

  <footer class="pagination-container">
    <pagination-controls (pageChange)="currentPage = $event"></pagination-controls>
  </footer>

</section>
