import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Immagine } from 'src/app/model/immagine/immagine';
import { Post } from 'src/app/model/post/post';
import { Utente } from 'src/app/model/utente/utente';
import { PostService } from 'src/app/services/post-Service/post.service';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';

@Component({
  selector: 'app-singolo-post',
  templateUrl: './singolo-post.component.html',
  styleUrls: ['./singolo-post.component.css']
})
export class SingoloPostComponent implements OnInit{
  @Input() post?: Post;
  autore?: String;
  categorie?: String;
  listaImmagini: Immagine[] = [];
  immagineURL?: string;
  dataPost?: string;

  constructor(private route: ActivatedRoute, private service: PostService, private serviceUtente: UtenteService , 
    private router: Router) { }

  ngOnInit(): void {
    this.service.getAutorePostById(this.post!.id_post!)
    .subscribe({
      next: (response) => {
        this.autore = response;

        this.service.getCategoriePostById(this.post!.id_post!)
          .subscribe({
            next: (responseCategory) => {
              this.categorie = responseCategory;

              /*  Nel caso di piu categorie

                  const keys = Object.keys(responseCategory);
                  for (let i = 0; i < keys.length; i++) {
                    const key = keys[i];
                    this[`categoria${i + 1}`] = responseCategory[key];
                  }
               * 
               */

              this.service.getImmaginiPostById(this.post!.id_post!)
                .subscribe({
                  next: (responseImage) => {
                    this.listaImmagini = responseImage.immaginiAssociate!;
                    for (let i = 0; i < this.listaImmagini.length; i++) {
                      if(this.immagineURL !== 'null'){ //credo qui vada corretto con this.listaImmagini[i]
                        this.immagineURL = this.listaImmagini[i].url;
                        break; // Per assegnare solo la prima immagine non-null
                      }
                    }

                  },
                  error: (error) => console.log(error)
                });                                                 
            },

            error: (error) => console.log(error)
          });
      },

      error: (error) => console.log(error)
    });
  }

  setDataFormat(){
    const currentDatePost = this.post!.data!;

    // Estrai il giorno, il mese e l'anno dalla data corrente
    const day = currentDatePost.getDate().toString().padStart(2, '0'); // Aggiunge lo zero iniziale se il giorno è a una sola cifra
    const month = (currentDatePost.getMonth() + 1).toString().padStart(2, '0'); // Il mese in JavaScript è indicizzato da 0 (Gennaio) a 11 (Dicembre), quindi aggiungi 1
    const year = currentDatePost.getFullYear().toString();

    // Crea la stringa con il formato giorno-mese-anno
    const formattedDate = `${day}-${month}-${year}`;

    this.dataPost = formattedDate;
  }

  isContentExpanded: boolean = false;

  toggleContentExpand(): void {
    this.isContentExpanded = !this.isContentExpanded;
  }

  openPost(){

  }

}
<div id="contenuto-post">
  <div class="col">
    <div class="card" >

      <img *ngIf="immagineURL" [src]="immagineURL" class="card-img-top" alt="Image"/>

      <div class="card-header">
        <h6 class="card-data" style="display: inline;">{{post?.data}}</h6>
        <h6 class="card-category" style="display: inline; margin-left: 160px;">{{categorie}}</h6>
        <h5 class="card-title" style="margin-top: 20px;">{{post?.titolo}}</h5>
        <h5 class="card-writer" style="margin-top: 20px;">{{autore}}</h5>        
      </div>

      <div class="card-body">
        <!-- Indichiamo di mostrare il contenuto del post, ma se il contenuto del testo supera i 100 caratteri 
          di mostrare poi i "..." per indicare che il testo continua e al click sulla card aprire la pagina di 
          riferimento del singolo post.-->
        <p class="card-text">{{post?.contenuto | slice:0:200}}{{post?.contenuto!.length > 100 ? ' [...]' : ''}}</p>
        <a class="expand-link" *ngIf="post?.contenuto!.length > 100" (click)="openPost()">Espandi</a>
      </div>

    </div>
  </div>
</div>
