  <!-- 2 visualizzazione -->

  <div class="container">
    <div id="post-carousel" class="carousel slide" data-ride="carousel">
      <ol class="carousel-indicators">
        <li *ngFor="let post of listaPost; let i = index" [attr.data-target]="'#post-carousel'" [attr.data-slide-to]="i" [class.active]="i === 0"></li>
      </ol>
      <div class="carousel-inner">
        <div class="carousel-item" *ngFor="let post of listaPost; let i = index" [class.active]="i === 0">
          <img *ngIf="post.listaImmagini.length > 0" [src]="post.listaImmagini[0].url" class="d-block w-100" alt="Image">
          <div class="carousel-caption">
            <h5>{{ post.titolo }}</h5>
            <p>{{ post.contenuto }}</p>
            <button type="button" class="btn btn-outline-success" (click)="aggiungiPreferito(post)">Aggiungi ai Preferiti</button>
          </div>
        </div>
      </div>
      <a class="carousel-control-prev" href="#post-carousel" role="button" data-slide="prev">
        <span class="carousel-control-prev-icon" aria-hidden="true"></span>
        <span class="sr-only">Previous</span>
      </a>
      <a class="carousel-control-next" href="#post-carousel" role="button" data-slide="next">
        <span class="carousel-control-next-icon" aria-hidden="true"></span>
        <span class="sr-only">Next</span>
      </a>
    </div>
  </div>
