import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Utente } from 'src/app/model/utente/utente';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';

@Component({
  selector: 'app-login-utente',
  templateUrl: './login-utente.component.html',
  styleUrls: ['./login-utente.component.css']
})
export class LoginUtenteComponent implements OnInit{
  utente: Utente = new Utente();

  constructor(private service: UtenteService, private router: Router) {}

  ngOnInit(): void {
    const video = document.getElementById('background-video') as HTMLVideoElement;
    video.play();
  }

  onSubmit() : void {
    console.log(this.utente);
    this.accessoUtente();
  }

  private accessoUtente() {
      this.service.loginUtente(this.utente)
          .subscribe({
            next: data => {
              console.log(data);
              sessionStorage.setItem('utenteloggato', JSON.stringify(data));

              this.utente = new Utente();

              this.amministratore();

              this.router.navigate(['/','homepage']).then
              (() => window.location.reload());
            },
            error: (err) => {
              switch(err.status){
                case 406:
                  this.showErrorLogin();
                  break;
                default:
                  console.log(err);
              }
            }
          });
  }

  amministratore(){
    const admin = JSON.parse(sessionStorage.getItem('utenteloggato')!);
    if (admin.email === 'gemelli.95@hotmail.it' && admin.password === 'Wanderlust_2023') {
      sessionStorage.setItem('administrator', 'Sei un amministratore.');
    }
  }

  errorLogin: boolean = false;

  showErrorLogin(){
    this.errorLogin = true;
    // Il messaggio scompare dopo 4 secondi
    setTimeout(() => {
      this.errorLogin = false;
    }, 4000);
  }

}
<section id="wrapper">

  <div id="video">
    <video id="background-video" [muted]="'muted'" loop autoplay>
      <source src="/assets/Video/Mare_Video_1.mp4" type="video/mp4">
    </video>
  </div>

  <div id="inner-wrapper" class="container">
    <div id="content-wrapper" class="js-content-wrapper">

      <section id="main">
        <div id="content" class="page-content">
          <section class="login-form d-flex justify-content-center align-items-center">
            <div class="form-signin w-100 m-auto">

              <form autocomplete="off" (ngSubmit)="onSubmit()">

                <div style="padding-bottom: 10px;">
                  <h1 class="h3 mb-3 fw-normal" style="display: inline; font-family: fantasy; font-size: xxx-large;">Welcome Back</h1>
                </div> 

                <div class="form-floating">
                  <input type="email" class="form-control" id="floatingInput" placeholder="Email" required [(ngModel)]="utente.email" name="email">
                  <label for="floatingInput">Email</label>
                </div>

                <div class="form-floating">
                  <input type="password" class="form-control" id="floatingPassword" placeholder="Password" required [(ngModel)]="utente.password" name="password">
                  <label for="floatingPassword">Password</label>
                </div>  

                <div class="form-check text-start my-3">
                  <input class="form-check-input" type="checkbox" value="remember-me" id="flexCheckDefault">
                  <label class="form-check-label" for="flexCheckDefault">
                    Remember me
                  </label>
                </div>

                <button class="btn btn-primary w-100 py-2" type="submit" value="login">Sign in</button>

                <p class="mt-5 mb-3 text-body-secondary">Non hai un account? 
                  <a href="/registrazione" style="color: blue;">Registrati</a>.
                </p>


                <div class="error-message" *ngIf="errorLogin">
                  L'accesso non è andato a buon fine. Assicurarsi di essere registrati e/o controllare dati di accesso.
                </div>

              </form>

            </div>
          </section>
        </div>
      </section>

    </div>
  </div>

</section>
