import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Categoria } from 'src/app/model/categoria/categoria';
import { Post } from 'src/app/model/post/post';
import { Utente } from 'src/app/model/utente/utente';
import { CategoriaService } from 'src/app/services/categoria-Service/categoria.service';
import { ImmagineService } from 'src/app/services/immagine-Service/immagine.service';
import { PostService } from 'src/app/services/post-Service/post.service';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';

@Component({
  selector: 'app-i-miei-post',
  templateUrl: './i-miei-post.component.html',
  styleUrls: ['./i-miei-post.component.css']
})
export class IMieiPostComponent implements OnInit{
  utente?: Utente;
  currentPost?: Post;
  newPost: Post = new Post();
  categorie?: Categoria[] = [];

  constructor(private route: ActivatedRoute, private service: UtenteService, private servicePost: PostService, 
    private serviceCategoria: CategoriaService, private router: Router, private http: HttpClient) {}

  ngOnInit(): void {
    this.utente = JSON.parse(sessionStorage.getItem('utenteloggato')!)
    this.service
      .getArticoliUtenteById(this.utente!.id_utente!)
      .subscribe(
        {
          next: data => {
                  console.log(data);
                  this.utente = data;
                },
          error: error => console.log(error)
        }
      );

      this.serviceCategoria.getAllCategorie().subscribe({
        next: data => {
          console.log(data);
          this.categorie = data;
        },
        error: error => console.log(error)
      })    
  }

  activateAddPost: boolean = false;

  aggiuntaPost(){    
    this.activateAddPost = true;
  }  

  activateUpdatePost: boolean = false;

  aggiornamentoPost(postVecchio: Post){
    this.currentPost = postVecchio;
    this.activateUpdatePost = true;
  }

  eliminazionePost(id_post?: number){
    if (id_post) {
      this.servicePost.deletePostById(id_post)
      .subscribe({
        next: () => {
          console.log('Post eliminato con successo.');
          this.router.navigate(['/','mieiarticoli']).then
              (() => window.location.reload());

        },
        error: error => console.log(error)
      });
    }
  }

  onSubmitAdd(){
    console.log(this.newPost);
    this.savePost();
  }

  savePost(){
    this.servicePost.addPost(this.newPost, this.utente!.id_utente!)
          .subscribe({
            next: (data) => {
              console.log(data);
              this.newPost = new Post();
              this.router.navigate(['/','mieiarticoli']).then
              (() => window.location.reload());
            },
            error: (err) => console.log(err)
          });

  }

  onSubmitUpdate(){
    console.log(this.currentPost);
    this.aggiornaPost();
  }

  aggiornaPost(){
    this.servicePost.updatePost(this.currentPost!, this.utente!.id_utente!)
      .subscribe(
        {
          next: data => {
                  console.log(data);
                  this.currentPost = data;
                  this.router.navigate(['/','mieiarticoli']).then
                  (() => window.location.reload());
                },
          error: (err) => console.log(err)
        }
      );
  }







}
<section id="wrapper">
    <div class="wrapper">
        <div class="container" *ngFor="let post of utente?.articoli">
            <app-singolo-post [post]="post"></app-singolo-post>
                <button type="button" class="btn btn-outline-danger" (click)="eliminazionePost(post?.id_post)">Rimuovi Post</button>
                <button type="button" class="btn btn-outline-warning" (click)="aggiornamentoPost(post)">Aggiorna Post</button>         

                <!-- Aggiornamento di un Post -->
        <ng-container *ngIf="activateUpdatePost">
            <section class="card-lg__container">
                <div class="card__content">
                    <form (ngSubmit)="onSubmitUpdate()" class="row g-3">

                        <div style="padding-bottom: 10px;">
                            <h3 class="h3 mb-3 fw-normal" style="display: inline; font-family: fantasy; font-size: xxx-large;">Aggiornamento Post</h3>
                        </div>

                        <div class="col-md-4">
                            <div class="form-floating" >
                              <input type="text" class="form-control input-md" id="floatingTitolo" placeholder="Titolo" required [(ngModel)]="currentPost!.titolo" name="titolo" />
                              <label for="floatingInput">Titolo</label>
                            </div>
                        </div>

                        <div class="col-md-4">
                            <div class="form-floating" >
                              <textarea type="text" class="form-control input-md" id="floatingContenuto" placeholder="Contenuto" required [(ngModel)]="currentPost!.contenuto" name="contenuto"></textarea>
                              <label for="floatingInput">Contenuto</label>
                            </div>
                        </div>

                        <select class="form-select" name="subject" aria-label="Subject" [(ngModel)]="currentPost!.categorie" id="selectCategoria">
                            <option disabled selected>Seleziona una Categoria</option>
                            <ng-container *ngFor="let categoria of categorie">
                                <option [ngValue]="categoria">{{categoria!.nome_categoria}}</option>
                            </ng-container>
                        </select>


                        <!-- Immagini -->
                        <!-- <div>
                            <label class="form-label" for="customFile">Aggiungi un' immagine</label>
                            <input type="file" class="form-control" id="customFile" (change)="onFileSelected($event)" multiple/>
                        </div> -->


                        <button class="btn btn-primary w-100 py-2" type="submit" value="updatingPost" >Aggiorna</button>
                    </form>
                </div>
            </section>
        </ng-container>

        <!-- Aggiunta di un Post -->
        <ng-container *ngIf="activateAddPost">
            <section class="card-lg__container">
                <div class="card__content">
                    <form (ngSubmit)="onSubmitAdd()" class="row g-3">

                        <div style="padding-bottom: 10px;">
                            <h3 class="h3 mb-3 fw-normal" style="display: inline; font-family: fantasy; font-size: xxx-large;">Aggiungi un Post</h3>
                        </div>

                        <div class="col-md-4">
                            <div class="form-floating" >
                              <input type="text" class="form-control input-md" id="floatingTitolo" placeholder="Titolo" required [(ngModel)]="newPost.titolo" name="titolo" />
                              <label for="floatingInput">Titolo</label>
                            </div>
                        </div>

                        <div class="col-md-4">
                            <div class="form-floating" >
                              <textarea type="text" class="form-control input-md" id="floatingContenuto" placeholder="Contenuto" required [(ngModel)]="newPost.contenuto" name="contenuto"></textarea>
                              <label for="floatingInput">Contenuto</label>
                            </div>
                        </div>

                        <select class="form-select" name="subject" aria-label="Subject" [(ngModel)]="newPost.categorie" id="selectCategoria">
                            <option disabled selected>Seleziona una Categoria</option>
                            <ng-container *ngFor="let categoria of categorie">
                                <option [ngValue]="categoria">{{categoria!.nome_categoria}}</option>
                            </ng-container>
                        </select>


