import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { throwError } from 'rxjs';
import { Categoria } from 'src/app/model/categoria/categoria';
import { Immagine } from 'src/app/model/immagine/immagine';
import { Post } from 'src/app/model/post/post';
import { Utente } from 'src/app/model/utente/utente';
import { CategoriaService } from 'src/app/services/categoria-Service/categoria.service';
import { ImmagineService } from 'src/app/services/immagine-Service/immagine.service';
import { PostService } from 'src/app/services/post-Service/post.service';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';
import { format } from 'date-fns';

@Component({
  selector: 'app-post-management',
  templateUrl: './post-management.component.html',
  styleUrls: ['./post-management.component.css']
})
export class PostManagementComponent implements OnInit{  
  utente: Utente = new Utente();
  listaPost: Post[] = [];
  listaPostBackup: Post[] = [];
  categorie?: Categoria[] = [];

  currentPage: number = 1;

  constructor(private route: ActivatedRoute, private service: PostService, private serviceUtente:UtenteService,
    private router: Router, private serviceCategoria: CategoriaService) {}

  ngOnInit(): void {
    this.service.getAllPosts()
    .subscribe({
      next: (response: Post[]) => {
        this.listaPost = response;
        this.listaPostBackup = response;

        this.serviceCategoria.getAllCategorie().subscribe({
          next: data => {
            data.sort((a, b) => a.nome_categoria!.localeCompare(b.nome_categoria!)); //ordine alfabetico categorie
            this.categorie = data;
          },
          error: error => console.log(error)
        })
      },
      error: (error: HttpErrorResponse) => {
        alert(error.message)
      }          
    });      
  }

  goBack() {
    window.history.back();
  }

  noPost: boolean = false;

  ricercaPostPerTitolo(key: string): void {
    console.log(key);
    const results: Post[] = [];
    for (const post of this.listaPost) {
      if (post.titolo!.toLowerCase().indexOf(key.toLowerCase()) !== -1) {
        results.push(post);
      }
    }
    //aggiorniamo i post visibili sulla pagina
    this.listaPost = results;
    //se non c'è alcuna ricerca effettuata o il risultato è nullo ridammi tutti i post
    if (!key) {
      this.noPost = false;
      this.listaPost = this.listaPostBackup;
    }

    if (results.length === 0 && key) {
      this.noPost = true;
    }
  }

  eliminazionePost(id_post?: number){
    if (id_post) {
      this.service.deletePostById(id_post)
      .subscribe({
        next: () => {
          console.log('Post eliminato con successo.');
          this.router.navigate(['/','mieiarticoli']).then
              (() => window.location.reload());

        },
        error: error => console.log(error)
      });
    }
  }

  // Creiamo una mappa che associa ad ogni postId un booleano
  postMenuStatus: { [postId: number]: boolean } = {};

  toggleMenuPost(postId: number) {
    if (this.postMenuStatus[postId] === undefined) { //se non c'è corrispondenza nella mappa allora inserisce il valore
        this.postMenuStatus[postId] = false;
    }
    this.postMenuStatus[postId] = !this.postMenuStatus[postId]; //se invece è presente un valore, deve cambiarlo
  }

}
<section id="wrapper">

  <header class="page-header">

    <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 512 512" style="font-size: x-large; cursor: pointer;" (click)="goBack()">
      <path d="M48 256a208 208 0 1 1 416 0A208 208 0 1 1 48 256zm464 0A256 256 0 1 0 0 256a256 256 0 1 0 512 0zM217.4 376.9c4.2 4.5 10.1 7.1 16.3 7.1c12.3 0 22.3-10 22.3-22.3V304h96c17.7 0 32-14.3 32-32V240c0-17.7-14.3-32-32-32H256V150.3c0-12.3-10-22.3-22.3-22.3c-6.2 0-12.1 2.6-16.3 7.1L117.5 242.2c-3.5 3.8-5.5 8.7-5.5 13.8s2 10.1 5.5 13.8l99.9 107.1z"/>
    </svg>

    <h1 class="h1 page-title">
      <span>Articoli Pubblicati</span>
    </h1>

  </header>

    <!-- Parte centrale Post -->
  <div class="filter-posts-container">

    <div class="posts-column">

      <!-- Messaggio Nessun Post trovato -->
      <div *ngIf="noPost" class="col-lg-12 col-md-12 col-xl-12" style="width: auto; padding-left: 40px; padding-right: 40px;">
        <div class="alert alert-info" role="alert">
          <h4 class="alert-heading">NESSUN ARTICOLO TROVATO!</h4>
          <p>No Posts were found.</p>
        </div>
      </div>

      <!-- tramite la angular class indichiamo che la classe cambia nel momento in cui no-posts diventa true
      e noPost diventa true quando non ci sono posts da mostrare. -->
      <div class="container" [ngClass]="{'no-posts': noPost}">
        <div class="row">
          <!-- paginate: Questa è la pipe di paginazione fornita dal modulo ngx-pagination. Una pipe è una funzione
            che può essere utilizzata per trasformare i dati prima di visualizzarli. La pipe paginate gestisce la
            suddivisione dei dati in pagine in base ai parametri specificati. L'oggetto che passiamo alla pipe è tra le {} -->
          <div class="col-12 col-md-4" *ngFor="let post of (listaPost | paginate: { itemsPerPage: 9, currentPage: currentPage })!">

            <div class="menu-post">
              <input class="toggle-check" id="checkbox-post-{{post.id_post}}" type="checkbox" (click)="toggleMenuPost(post.id_post!)">
                  <label class="toggle-post" for="checkbox-post-{{post.id_post}}">
                      <div id="bar1" class="bars"></div>
                      <div id="bar2" class="bars"></div>
                      <div id="bar3" class="bars"></div>
                  </label>
            </div>

            <div class="container-button" *ngIf="postMenuStatus[post.id_post!]">

              <button class="btn-delete" (click)="eliminazionePost(post?.id_post)">
                <svg viewBox="0 0 15 17.5" height="17.5" width="15" xmlns="http://www.w3.org/2000/svg" class="icon-delete">
                    <path transform="translate(-2.5 -1.25)" d="M15,18.75H5A1.251,1.251,0,0,1,3.75,17.5V5H2.5V3.75h15V5H16.25V17.5A1.251,1.251,0,0,1,15,18.75ZM5,5V17.5H15V5Zm7.5,10H11.25V7.5H12.5V15ZM8.75,15H7.5V7.5H8.75V15ZM12.5,2.5h-5V1.25h5V2.5Z" id="Fill"></path>
                </svg>
              </button>

            </div>

            <app-singolo-post [post]="post"></app-singolo-post>

          </div>

        </div>
      </div>

    </div>

  </div>

  <footer class="pagination-container">
    <pagination-controls (pageChange)="currentPage = $event"></pagination-controls>
  </footer>

</section>
