import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Immagine } from 'src/app/model/immagine/immagine';

@Injectable({
  providedIn: 'root'
})
export class ImmagineService {

  private baseUrl = 'http://localhost:8080/api/v4';

  constructor(private http: HttpClient) { }

  addImmagine(immagine: FormData): Observable<Immagine> {
    const headers = new HttpHeaders();
    headers.append('Accept', 'application/json');

    return this.http.post<Immagine>(`${this.baseUrl}/add/i`, Immagine, { headers });
  }
}
