import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Utente } from 'src/app/model/utente/utente';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';

@Component({
  selector: 'app-registrazione-utente',
  templateUrl: './registrazione-utente.component.html',
  styleUrls: ['./registrazione-utente.component.css']
})

export class RegistrazioneUtenteComponent implements OnInit {
  utente: Utente = new Utente();

  constructor(private service: UtenteService, private router: Router) { }

  ngOnInit(): void {
    const video = document.getElementById('background-video') as HTMLVideoElement;
    video.play();
  }

  onSubmit() : void {
    console.log(this.utente);
    this.saveUtente();
  }

  private saveUtente() {
      this.service.addUtente(this.utente)
          .subscribe({
            next: data => {
              console.log(data);
              this.utente = new Utente();
            },
            error: error => console.log(error)
          });  
  }
}
<section id="wrapper">
  <div id="video">
    <video id="background-video" loop muted>
      <source src="/assets/Video/Mare_Video_1.mp4" type="video/mp4">
    </video>
  </div>
  <div id="inner-wrapper" class="container">
    <div id="content-wrapper" class="js-content-wrapper">
      <section id="main">
        <div id="content" class="page-content">
          <section class="registration-form d-flex justify-content-center align-items-center">
            <div class="form-signup w-100 m-auto">
              <form autocomplete="off" (ngSubmit)="onSubmit()" class="row g-3">
                <div style="padding-bottom: 10px;">
                  <h1 class="h3 mb-3 fw-normal" style="display: inline; font-family: fantasy; font-size: xxx-large;">Completa la Registrazione</h1>
                </div>
                <div class="col-md-6">
                  <div style="display: inline;" class="form-floating" >
                    <input type="nome" class="form-control" id="floatingInput" placeholder="Nome" required [(ngModel)]="utente.nome" name="nome">
                    <label for="floatingInput">Nome</label>
                  </div>
                </div>
                <div class="col-md-6">
                  <div class="form-floating">
                    <input type="cognome" class="form-control" id="floatingInput" placeholder="Cognome" required [(ngModel)]="utente.cognome" name="cognome">
                    <label for="floatingInput">Cognome</label>
                  </div>
                </div>
                <div class="col-md-12">
                  <div class="form-floating">
                    <input type="email" class="form-control" id="floatingInput" placeholder="Email" required [(ngModel)]="utente.email" name="email">
                    <label for="floatingInput">Email</label>
                  </div>
                </div>
                <div class="col-md-12">
                  <div class="form-floating">
                    <input type="password" class="form-control" id="floatingPassword" placeholder="Password" required [(ngModel)]="utente.password" name="password">
                    <label for="floatingPassword">Password</label>
                  </div>
                </div>
                <div class="col-md-12">         
                  <div class="form-check text-start my-3">
                    <input class="form-check-input" type="checkbox" value="subscribe-to-newsletter" id="flexCheckDefault">
                    <label class="form-check-label" for="flexCheckDefault">
                      Iscriviti alla Newsletter
                    </label>
                  </div>
                </div>
                <button class="btn btn-primary w-100 py-2" type="submit" value="registration">Sign up</button>
              </form>
            </div>
          </section>
        </div>
      </section>
    </div>
  </div>
</section>
