import { Component, OnInit } from '@angular/core';
import { Utente } from 'src/app/model/utente/utente';

@Component({
  selector: 'wanderlust-header',
  templateUrl: './wanderlust-header.component.html',
  styleUrls: ['./wanderlust-header.component.css']
})
export class WanderlustHeaderComponent implements OnInit{
  utente?: Utente;

  ngOnInit(): void {    
    if(sessionStorage.getItem('utenteloggato')){
      this.utente = JSON.parse(sessionStorage.getItem('utenteloggato')!)
    }
  }

  logout(): void {
    sessionStorage.removeItem('utenteloggato');
    window.location.reload();
  }

}
<div class="header">
    <img src="assets/Immagini/Logo_Wanderlust_2.png" alt="Logo" class="logo">
    <h2 style="color: black; display: inline-block; vertical-align: middle; margin-top: 5px; margin-left: 10px; border-bottom: solid; font-size: xx-large;">Wanderlust</h2>
    <ng-container *ngIf="utente?.username; else notLogged">
        <div class="navbar-text">
            {{student?.username}}
            <button (click)="logout()" class="btn btn-secondary">Logout</button>
        </div>
    </ng-container>
</div>
