import { Component } from '@angular/core';

@Component({
  selector: 'wanderlust-header',
  templateUrl: './wanderlust-header.component.html',
  styleUrls: ['./wanderlust-header.component.css']
})
export class WanderlustHeaderComponent {

}
<div class="header">
  <div class="row align-items-center">
    <div class="logo-container">
      <img src="/Wanderlust_FrontEnd/src/assets/Immagini/Logo_Wanderlust.png" alt="logo">
    </div>
  </div>
</div>
