import { Component, OnInit } from '@angular/core';
import { Utente } from 'src/app/model/utente/utente';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css']
})
export class HomePageComponent implements OnInit{
  utente?: Utente;

  videoPath?: string;
  largeCardTitle?: string;
  largeCardText?: string;
  largeCardDescription?: string;

  constructor() {}

  ngOnInit(): void {
    this.setSelectedCard(this.card1);
    console.log(this.videoPath);
  }

  selectedCard: any;

  setSelectedCard(card: any) {
    this.selectedCard = card;
  }

  setVideoBackground(percorsoVideo: string){
    this.videoPath = percorsoVideo;


    console.log(this.videoPath);

  }


  card1 = {
    videoPath: '/assets/Video/Animal_Video_1.mp4',
    largeCardTitle: 'Sierra Desert - Marocco',
    largeCardText: 'Marrakech Merzouga',
    largeCardDescription: 'Duis aute irure dolor in reprehenderit in voluptate nulla pariatur.'
  };

  card2 = {
    videoPath: '/assets/Video/Venezia.mp4',
    largeCardTitle: 'Venezia - Italia',
    largeCardText: 'Laguna di Venezia',
    largeCardDescription: 'Duis aute irure dolor in reprehenderit in voluptate nulla pariatur.'
  };

  card3 = {
    videoPath: '/assets/Video/Niagara.mp4',
    largeCardTitle: 'Niagara Falls - USA',
    largeCardText: 'Cascate del Niagara',
    largeCardDescription: 'Duis aute irure dolor in reprehenderit in voluptate nulla pariatur.'
  };

  card4 = {
    videoPath: '/assets/Video/Cappadocia_Video_1.mp4',
    largeCardTitle: 'Cappadocia - Turchia',
    largeCardText: 'Valle di Goreme',
    largeCardDescription: 'Duis aute irure dolor in reprehenderit in voluptate nulla pariatur.'
  };


}
<section id="wrapper">
    <div class="header-homepage">
      <!-- /assets/Video/Animal_Video_1.mp4 -->
      <div id="video">
        <video id="background-video" [muted]="'muted'" loop autoplay>
            <source [src]="videoPath" type="video/mp4">
        </video>
      </div>

      <div id="inner-wrapper" class="container">
        <div id="content-wrapper" class="js-content-wrapper">
          <section id="main">
            <div id="content" class="page-content">
              <section class="home-page d-flex justify-content-center align-items-center">

                <div id="contenuto-uno">
                  <section class="card-lg__container">
                    <div class="card__content">
                        <p class="card__text-sm">{{ selectedCard?.largeCardTitle }}</p>
                        <h1 class="card__text-lg">{{ selectedCard?.largeCardText }}</h1>
                        <p class="card__text-md">{{ selectedCard?.largeCardDescription }}</p>
                        <a href="/listapost" class="card__btn ">Esplora</a>
                    </div>
                  </section>
                </div>

                <div id="contenuto-due">
                  <section class="card-sm__container">

                    <section class="card-sm" (click)="setSelectedCard(card1)" (click)="setVideoBackground()" style="background-image:url('/assets/Immagini/Deserto_1.jpg')">
                      <p class="card__text-sm">Sierra Desert - Marocco</p>
                      <h1 class="card__text-lg">Marrakech Merzouga</h1>
                    </section>

                    <section class="card-sm" (click)="setSelectedCard(card2)" style="background-image: url('/assets/Immagini/Galleria_1.jpg'); background-position: center right;">
                      <p class="card__text-sm">Venezia - Italia</p>
                      <h1 class="card__text-lg">Laguna di Venezia</h1>
                    </section>

                    <section class="card-sm" (click)="setSelectedCard(card3)" style="background-image: url('/assets/Immagini/Galleria_7.jpg')">
                      <p class="card__text-sm">Niagara Falls - USA</p>
                      <h1 class="card__text-lg">Cascate del Niagara</h1>
                    </section>

                    <section class="card-sm" (click)="setSelectedCard(card4)" style="background-image: url('/assets/Immagini/Cappadocia_1.jpg')">
                      <p class="card__text-sm">Cappadocia - Turchia</p>
                      <h1 class="card__text-lg">Valle di Goreme</h1>
                    </section>

                  </section>

                </div>

              </section>
            </div>
          </section>
        </div>
      </div>        
    </div>

    <div class="footer-homepage">
      <h1>Ciao</h1>
    </div>


  </section>
