import { Component, OnInit } from '@angular/core';
import { Utente } from 'src/app/model/utente/utente';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css']
})
export class HomePageComponent implements OnInit{

  //da spostare nell'header
  utente?: Utente;

  ngOnInit(): void {    
    if(sessionStorage.getItem('utenteloggato')){
      this.utente = JSON.parse(sessionStorage.getItem('utenteloggato')!)
    }
  }

  logout(): void {
    sessionStorage.removeItem('utenteloggato');
    window.location.reload();
  }


}
<section id="wrapper">
    <div class="header-homepage">

      <div id="video">
        <video id="background-video" [muted]="'muted'" loop autoplay>
            <source src="/assets/Video/Animal_Video_1.mp4" type="video/mp4">
        </video>
      </div>

      <div id="inner-wrapper" class="container">
        <div id="content-wrapper" class="js-content-wrapper">
          <section id="main">
            <div id="content" class="page-content">
              <section class="login-form d-flex justify-content-center align-items-center">
                <div class="form-signin w-100 m-auto">


                </div>
              </section>
            </div>
          </section>
        </div>
      </div>

    </div>

    <div class="footer-homepage">
      <h1>Ciao</h1>
    </div>
  </section>
