import { HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Utente } from 'src/app/model/utente/utente';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';

//Qui sono specificati i file che sono direttamente connessi con questo e quindi 
//accedono direttamente a quanto dichiarato qui
@Component({
  selector: 'app-lista-utenti',
  templateUrl: './lista-utenti.component.html',
  styleUrls: ['./lista-utenti.component.css']
})
export class ListaUtentiComponent implements OnInit{
  utenti?: Utente[];

  constructor(private route: ActivatedRoute, private service: UtenteService, private router: Router) { }

  //quando viene chiamato il componente esegui il metodo descritto
  ngOnInit(): void{
    this.reloadData();
  }

  reloadData(): void {
    this.service.getAllUtenti()
        .subscribe({
          next: (response: Utente[]) => {
            this.utenti = response.sort((a,b) => a.cognome!.localeCompare(b.cognome!));
          },
          error: (error: HttpErrorResponse) => {
            alert(error.message)
          }
        });
  }

  dettagliUtente(id?: number){
    this.router.navigate(["dettagliutente", id]);
  }

  rimuoviUtente(id?: number){ 
    if (id) {
      this.service.deleteUtenteById(id).subscribe({
        next: () => {
          console.log('Utente eliminato con successo.');
          this.reloadData(); // Aggiorna la lista degli utenti dopo l'eliminazione
        },
        error: error => console.log(error)
      });
    }
  }

  goBack() {
    window.history.back();
  }

}
<section id="wrapper">

  <header class="page-header">

    <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 512 512" style="font-size: x-large; cursor: pointer;" (click)="goBack()">
      <path d="M48 256a208 208 0 1 1 416 0A208 208 0 1 1 48 256zm464 0A256 256 0 1 0 0 256a256 256 0 1 0 512 0zM217.4 376.9c4.2 4.5 10.1 7.1 16.3 7.1c12.3 0 22.3-10 22.3-22.3V304h96c17.7 0 32-14.3 32-32V240c0-17.7-14.3-32-32-32H256V150.3c0-12.3-10-22.3-22.3-22.3c-6.2 0-12.1 2.6-16.3 7.1L117.5 242.2c-3.5 3.8-5.5 8.7-5.5 13.8s2 10.1 5.5 13.8l99.9 107.1z"/>
    </svg>

    <h1 class="h1 page-title">
      <span>Utenti Registrati</span>
    </h1>

  </header>

  <div class="input-wrapper">

    <button class="icon"> 
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" height="25px" width="25px">
        <path stroke-linejoin="round" stroke-linecap="round" stroke-width="1.5" stroke="#fff" d="M11.5 21C16.7467 21 21 16.7467 21 11.5C21 6.25329 16.7467 2 11.5 2C6.25329 2 2 6.25329 2 11.5C2 16.7467 6.25329 21 11.5 21Z"></path>
        <path stroke-linejoin="round" stroke-linecap="round" stroke-width="1.5" stroke="#fff" d="M22 22L20 20"></path>
      </svg>
    </button>
    <input placeholder="Ricerca per cognome.." class="input" name="text" type="text">

  </div>


  <table class="table table-striped">

    <thead>
      <tr>
        <th>Cognome</th>
        <th>Nome</th>
        <th>Email</th>
        <th>Azioni</th>
      </tr>
    </thead>

    <tbody>
      <!-- ngFor mi permette di ciclare sull'array di oggetti ottenuto in questo caso gli utenti -->
      <tr *ngFor="let utente of utenti">
        <td>{{ utente.cognome }}</td>
        <td>{{ utente.nome }}</td>
        <td>{{ utente.email }}</td>
        <td>
          <!-- In questo punto sta ciclando sull'array di utenti e per ognuno crea il pulsante.
          Vengono immagazzinati per ognuno i dati del singolo utente e da questo riconosce quale utente è-->
          <button (click)="dettagliUtente(utente.id_utente)"
              class="btn btn-info">Dettagli</button>
          <button (click)="rimuoviUtente(utente.id_utente)"
              class="btn btn-info" style="margin-left: 30px">Rimuovi</button>
        </td>
      </tr>
    </tbody>

  </table>

</section>
