import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-dettagli-utente',
  templateUrl: './dettagli-utente.component.html',
  styleUrls: ['./dettagli-utente.component.css']
})
export class DettagliUtenteComponent implements OnInit{
  utente?: Employee = new Employee();
  private id = 0;

  constructor(private route: ActivatedRoute, private employeeService: EmployeeService,
              private router: Router) {}

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];

    this.employeeService
      .getEmployeeById(this.id)
      .subscribe(
        {
          next: data => {
                  console.log(data);
                  this.employee = data;
                },
          error: error => console.log(error)
        }
      );
  }

  gotoEmployeeList() {
    this.router.navigate(['/employee']);  // localhost:4200/employee
  }

}
