import { Component, ComponentFactoryResolver, OnInit, ViewChild, ViewContainerRef } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Utente } from 'src/app/model/utente/utente';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';

@Component({
  selector: 'app-i-miei-post',
  templateUrl: './i-miei-post.component.html',
  styleUrls: ['./i-miei-post.component.css']
})
export class IMieiPostComponent implements OnInit{
  utente?: Utente;

  @ViewChild('aggiornamentoPost', { read: ViewContainerRef }) entry?: ViewContainerRef;


  constructor(private route: ActivatedRoute, private service: UtenteService, private router: Router, private resolver: ComponentFactoryResolver) {}

  ngOnInit(): void {
    this.utente = JSON.parse(sessionStorage.getItem('utenteloggato')!)
    this.service
      .getArticoliUtenteById(this.utente!.id_utente!)
      .subscribe(
        {
          next: data => {
                  console.log(data);
                  this.utente = data;
                },
          error: error => console.log(error)
        }
      );
  }
}
<section id="wrapper">
    <template #aggiornamentoPost>
    </template>
    <div class="wrapper">
        <div class="container" *ngFor="let post of utente?.articoli">
            <app-singolo-post [post]="post"></app-singolo-post>
                <button type="button" class="btn btn-outline-success" (click)="aggiuntaPost()">Aggiungi Post</button>
                <button type="button" class="btn btn-outline-danger" (click)="eliminazionePost(post?.id_post)">Rimuovi Post</button>
                <button type="button" class="btn btn-outline-warning" (click)="aggiornamentoPost()">Aggiorna Post</button>         
        </div>
    </div>
</section>
