import { Component, OnInit } from '@angular/core';
import { Utente } from 'src/app/model/utente/utente';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css']
})
export class HomePageComponent implements OnInit{
  utente?: Utente;

  videoPath?: string;
  largeCardTitle?: string;
  largeCardText?: string;
  largeCardDescription?: string;


  constructor() {
    this.videoPath = '/assets/Video/video1.mp4'; // Video di sfondo predefinito
    this.largeCardTitle = 'Sierra Desert - Marocco';
    this.largeCardText = 'Marrakech Merzouga'; // Testo predefinito della card grande
    this.largeCardDescription = 'Duis aute irure dolor in reprehenderit in voluptate nulla pariatur.'; // Descrizione predefinita della card grande
  }

  ngOnInit(): void {
  }



  changeVideo(card: any) {
    this.videoPath = card.videoPath;
    this.largeCardText = card.largeCardText;
    this.largeCardDescription = card.largeCardDescription;
  }


}
