import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Utente } from 'src/app/model/utente/utente';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';

@Component({
  selector: 'app-admin-page',
  templateUrl: './admin-page.component.html',
  styleUrls: ['./admin-page.component.css']
})
export class AdminPageComponent {
  utente?: Utente;

  constructor(private route: ActivatedRoute, private service: UtenteService,private router: Router){};


  ngOnInit(): void {    
    if(sessionStorage.getItem('utenteloggato')){
      this.utente = JSON.parse(sessionStorage.getItem('utenteloggato')!)
    }
  }

  entraUtenti(){
    this.router.navigate(['/listautenti']).then
    (() => window.location.reload());

  }

  entraPost(){
    this.router.navigate(['/gestionepost']).then
    (() => window.location.reload());

  }

}
<section id="wrapper">

    <header class="page-header">

        <h1 class="h1 page-title">
          <span>Amministrazione</span>
        </h1>

    </header>

    <section id="content-wrapper">

        <div class="card">
            <div class="card-details">
              <p class="text-title">Gestione Utenti</p>
              <p class="text-body">Qui potrai visionare gli utenti registrati nel sito e gestirne la rimozione</p>
            </div>
            <button class="card-button" (click)="entraUtenti()">Entra</button>
        </div>

        <div class="card">
            <div class="card-details">
              <p class="text-title">Gestione Articoli</p>
              <p class="text-body">Qui potrai controllare gli articoli pubblicati e gestirne la rimozione. </p>
            </div>
            <button class="card-button" (click)="entraPost()" >Entra</button>
        </div>

    </section>





</section>
