import { HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Post } from 'src/app/model/post/post';
import { Utente } from 'src/app/model/utente/utente';
import { PostService } from 'src/app/services/post-Service/post.service';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';

@Component({
  selector: 'app-lista-post',
  templateUrl: './lista-post.component.html',
  styleUrls: ['./lista-post.component.css']
})
export class ListaPostComponent implements OnInit{
  listaPost: Post[] = [];
  utente: Utente = new Utente();

  currentPage: number = 1;

  constructor(private route: ActivatedRoute, private service: PostService, private serviceUtente:UtenteService,
     private router: Router){

  }
  ngOnInit(): void {
    this.utente = JSON.parse(sessionStorage.getItem('utenteloggato')!);
    this.service.getAllPosts()
      .subscribe(
        {
          next: (response: Post[]) => {
            this.listaPost = response;
          },
          error: (error: HttpErrorResponse) => {
            alert(error.message)
          }
        }
      );
  }

  aggiungiPreferito(post: Post){
    this.serviceUtente.addPreferitoUtenteById(post, this.utente!.id_utente!)
    .subscribe({
      next: () => {
        console.log('Post aggiunto ai preferiti con successo.');
        this.router.navigate(['/','listapost']).then
            (() => window.location.reload());        
      },
      error: error => console.log(error)
    });
  }

  ricercaPost(key: string): void {
    console.log(key);
    const results: Post[] = [];
    for (const post of this.listaPost) {
      if (post.titolo!.toLowerCase().indexOf(key.toLowerCase()) !== -1
      // || employee.email.toLowerCase().indexOf(key.toLowerCase()) !== -1
      // || employee.phone.toLowerCase().indexOf(key.toLowerCase()) !== -1
      // || employee.jobTitle.toLowerCase().indexOf(key.toLowerCase()) !== -1
      ) {
        results.push(post);
      }
    }
    //aggiorniamo i post visibili sulla pagina
    this.listaPost = results;
    //se non c'è alcuna ricerca effettuata o 
    if (results.length === 0 || !key) {
      this.getEmployees();
    }
  }

}
<header class="page-header">
  <h1 class="h1 page-title">
    <span>I Racconti dei Viaggiatori</span>
  </h1>
</header>

<div class="container">
  <div class="row">
    <!-- paginate: Questa è la pipe di paginazione fornita dal modulo ngx-pagination. Una pipe è una funzione 
      che può essere utilizzata per trasformare i dati prima di visualizzarli. La pipe paginate gestisce la 
      suddivisione dei dati in pagine in base ai parametri specificati. L'oggetto che passiamo alla pipe è tra le {} -->
    <div class="col-12 col-md-4" *ngFor="let post of (listaPost | paginate: { itemsPerPage: 9, currentPage: currentPage })!">

      <app-singolo-post [post]="post"></app-singolo-post>

      <div *ngIf="this.utente" id="contenuto" style="margin-left: 5px;">
        <button type="button" class="btn btn-outline-success" (click)="aggiungiPreferito(post)">Aggiungi ai Preferiti</button>
      </div>

    </div>
  </div>
</div>

<div class="pagination-container">
  <pagination-controls (pageChange)="currentPage = $event"></pagination-controls>
</div>
