import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Utente } from 'src/app/model/utente/utente';
import { UtenteService } from 'src/app/services/utente-Service/utente.service';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-registrazione-utente',
  templateUrl: './registrazione-utente.component.html',
  styleUrls: ['./registrazione-utente.component.css']
})

export class RegistrazioneUtenteComponent implements OnInit {
  utente: Utente = new Utente();

  constructor(private service: UtenteService, private router: Router, private toastr: ToastrService) { }

  ngOnInit(): void {
    const video = document.getElementById('background-video') as HTMLVideoElement;
    video.play();
  }

  onSubmit() : void {
    console.log(this.utente);
    this.saveUtente();
  }

  private saveUtente() {
      this.service.addUtente(this.utente)
          .subscribe({
            next: data => {
              console.log(data);
              this.utente = new Utente();
            },
            error: error => console.log(error)
          });  
  }

  registrationSuccess() {
    this.toastr.success('La registrazione è stata effettuata con successo. Una mail di conferma è stata inviata all\'indirizzo specificato.');
  }
}
